var messageModule=angular.module("message",["app.config"]);messageModule.factory("MessageService",function(n,t){function i(i){var r=t.all(n.addMessage);return r.post(i)}function r(i){var r=t.one(n.getMessagesByPostId+"(PostId="+i+")");return r.get()}function u(i){var r=t.one(n.getMessagesByUserId+"(UserId="+i+")");return r.get()}return{addMessage:i,getMessagesByPostId:r,getMessagesByUserId:u}});messageModule.controller("MessageController",function(n,t){n.getMessages=function(n){return t.getMessagesByPostId(n)};n.addMessage=function(n){return t.addMessage(n)}});